    private void jbtTotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtTotalActionPerformed
       
        SubClass_child ItemCost = new SubClass_child();
        
        double iTax,iSubTotal, iTotal;
        
        ItemCost.Fish = ItemCost.pFish *Double.parseDouble(jTextFish.getText());
        ItemCost.ChickenBurger = ItemCost.pChickenBurger * Double.parseDouble(jTextChickenBurger.getText());
        ItemCost.ChickenLegend = ItemCost.pChickenLegend * Double.parseDouble(jTextChickenLegend.getText());
        ItemCost.CheeseBurger = ItemCost.pCheeseBurger * Double.parseDouble(jTextCheeseBurger.getText());
        
        ItemCost.MilkShake = ItemCost.pMilkShake* Double.parseDouble(jTextMilkShake.getText());
        ItemCost.VanillaCone = ItemCost.pVanillaCone* Double.parseDouble(jTextVanillaCone.getText());
        ItemCost.ChocolateMilkShake = ItemCost.pChocolateMilkShake * Double.parseDouble(jTextChocolateMilkShake.getText());
        ItemCost.Youghurt= ItemCost.pYoughurt * Double.parseDouble(jTextYoughurt.getText());
       
     iSubTotal = ItemCost.GetAmount();
     iTax = ItemCost.cFindTax(iSubTotal);
     iTotal =iSubTotal + iTax;
     
     String SubTotal = String.format("ksh%.2f", iSubTotal);
     jTextSubTotal.setText(SubTotal);
     jTextMealCost.setText(SubTotal);
     
     String Tax = String.format("ksh%.2f", iTax);
     jTextTax.setText(Tax);
     jTextDrinksCost.setText(Tax);
     
     String Total = String.format("ksh%.2f", iTotal);
     jTextTotal.setText(Total);
     jTextItemCost.setText(Total);
    
     
     
     
       String iMeal = String.format("ksh%.2f", ItemCost.Meals);
      jTextMealCost.setText(iMeal);
      
     
      
      String iDrink = String.format("ksh%.2f", ItemCost.Drinks);
      jTextDrinksCost.setText(iDrink);
      
      String TotalCost = String.format("ksh%.2f", ItemCost.TotalofMD);
      jTextItemCost.setText(TotalCost);
        
    }//GEN-LAST:event_jbtTotalActionPerformed

