    private void jbtreceiptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtreceiptActionPerformed
        // TODO add your handling code here:
        //============================================================================
        
        int refs=1325 + (int)( Math.random()*4238);
        //=============================================================================
        Calendar timer = Calendar.getInstance();
        timer.getTime();
        SimpleDateFormat tTime = new SimpleDateFormat("HH:mm:ss");
        tTime.format(timer.getTime());
        SimpleDateFormat Tdate = new SimpleDateFormat("dd-mm-yyyy");
        tTime.format(timer.getTime());
        
        jtxtreceipt.append("\tCafe System:\n" +
                "Reference:\t\t\t" +refs +
                "\n==========================================\n" +
                "Meals:\t\t" +jTextMealCost.getText()+"\n\n" +
                "Drinks:\t\t" +jTextDrinksCost.getText()+"\n\n" +
                "Cost of Drinks:\t\t" +jTextItemCost.getText()+"\n\n" +
                "\n==========================================\n" +
               "Tax:\t\t" +jTextTax.getText()+"\n\n" +
                "SubTotal:\t\t" +jTextSubTotal.getText()+"\n\n" +
                "Total:\t\t" +jTextTotal.getText()+"\n\n" +
                "\n==========================================\n" +
                "\nDate:" + Tdate.format(timer.getTime()) +
                "\tTime:" + tTime.format(timer.getTime()) +
                "\n\n\t\tThank you");
    }//GEN-LAST:event_jbtreceiptActionPerformed

