    private void jbtloginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtloginActionPerformed
        String Username=jTextusername.getText();
        String Password=jPassword.getText();
        
        if(Username.contains("shadrack")&&(Password.contains("shadrack9")))
        {
        this.setVisible(true);
        Cafe_System iCafe=new Cafe_System();
        iCafe.setVisible(true);
        }
        else if(Username.contains("shadrack")&&(Password.contains("shadrack9")))
        {
            jTextusername.setText("true");
            jPassword.setText("true");
            attempt +=3;
            
            if(attempt ==3);
            {
            System.exit(0);
            }
        }
    }//GEN-LAST:event_jbtloginActionPerformed

    private void jbtExitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtExitActionPerformed
        frame=new JFrame();
           if(JOptionPane.showConfirmDialog(frame,"Confirm if you want to exit","Cafe System",
                JOptionPane.YES_NO_OPTION )==JOptionPane.YES_NO_OPTION){
         System.exit(0);}
    }//GEN-LAST:event_jbtExitActionPerformed

